//Filter Script
$(function() {
	var selectedClass = "";
		$(".fil-cat").click(function(){ 
		selectedClass = $(this).attr("data-rel"); 
     $(".portFilter").fadeTo(100, 0.1);
		$(".portFilter div").not("."+selectedClass).fadeOut().removeClass('scale-anm');
    setTimeout(function() {
      $("."+selectedClass).fadeIn().addClass('scale-anm');
      $(".portFilter").fadeTo(300, 1);
    }, 300); 
		
	});
});

